import numpy as np

def generate_inner_angle_table():
    # Takes (x + (y^2/2x))/2 as input

    input_min = 0
    input_max = 1446
    table_size = (input_max-input_min)+1

    
    # Generate input values, scaled from the range [32768, 65535]
    inputs = np.linspace(input_min, input_max, table_size)
    
    # Compute cos^-1(input/65535) for each input
    cos_inverse_values = 2*np.arccos(inputs / (2*1024))
    
    # Multiply by 65535 to get the scaled values
    # Subtract 65535 here I think
    scaled_values = (65535/np.pi * cos_inverse_values).astype(int)
    
    # Create C-style array initialization string
    c_array = ', '.join(map(str, scaled_values))
    
    # Return the formatted C code for defining the lookup table
    return f"int inner_angle_table[{table_size}] = {{{c_array}}};"

def generate_tan_inverse_table():
    # Need PIC to find y*2^16/x beforehand

    table_size = 1023+1
    input_min = 0
    input_max = 1023

    # Generate input values, scaled from the range [32768, 65535]
    inputs = np.linspace(input_min, input_max, table_size)

    # Compute cos^-1(input/65535) for each input
    cos_inverse_values = np.arctan(inputs / (input_max))

    # Multiply by 65535 to get the scaled values
    scaled_values = (65535/np.pi * cos_inverse_values).astype(int)

    # Create C-style array initialization string
    c_array = ', '.join(map(str, scaled_values))
    
    # Return the formatted C code for defining the lookup table
    return f"int arctan_table[{table_size}] = {{{c_array}}};"

